import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import DashboardServices from "../../services/DashboardServices";
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import {
    ProductsContainer,
    ProductsWrapper,
    ProductsCard,
    ProductsIcon,
    ProductsH1,
    ProductsH2,
    ProductsP,
} from "./ProductElement";

function Problems() {
    const navigate = useNavigate();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filteredData, setFilteredData] = useState([]);
    const [category, setCategory] = useState("all");
    const [difficulty, setDifficulty] = useState("all");

    const handleProblemClick = (event, problem) => {
        console.log(event);
        navigate(`/problemDetails/${problem.id}`, { state: { problem } });
    };

    const getData = () => {
        DashboardServices.getAllProblems().then(response => {
            console.log(response.data);
            setData(response.data);
            setFilteredData(response.data);
            setLoading(false);
        }).catch(error => {
            console.log(error);
        });
    };

    // Filter problems based on category and difficulty
    useEffect(() => {
        let filtered = data;
        if (category !== "all") {
            filtered = filtered.filter(problem => problem.category === category);
        }
        if (difficulty !== "all") {
            filtered = filtered.filter(problem => problem.difficulty === difficulty);
        }
        setFilteredData(filtered);
    }, [category, difficulty, data]);

    useEffect(() => {
        getData();
    }, []);

    return (
        <>
            <ProductsContainer id="problems">
                <ProductsH1>Problems Dashboard</ProductsH1>
                {loading ? (
                    <div style={{ margin: "auto", top: "50%", display: "block" }}>
                        <Box sx={{ display: 'flex' }}>
                            <CircularProgress />
                        </Box>
                    </div>
                ) : (
                    <>
                        {/* Filters */}
                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
                            <select value={category} onChange={(e) => setCategory(e.target.value)} style={{ marginRight: '20px' }}>
                                <option value="all">All Categories</option>
                                <option value="Array">Array</option>
                                <option value="Linked List">Linked List</option>
                                <option value="Dynamic Programming">Dynamic Programming</option>
                                {/* Add more categories as needed */}
                            </select>

                            <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
                                <option value="all">All Difficulties</option>
                                <option value="easy">Easy</option>
                                <option value="medium">Medium</option>
                                <option value="hard">Hard</option>
                            </select>
                        </div>

                        {/* Problems List */}
                        <ProductsWrapper>
                            {filteredData.map((problem, i) => (
                                <ProductsCard key={i} onClick={event => handleProblemClick(event, problem)}>
                                    <ProductsIcon src="/images/placeholder-icon.png" /> {/* Use a placeholder image */}
                                    <ProductsH2>{problem.title}</ProductsH2>
                                    <ProductsP>{problem.description}</ProductsP>
                                </ProductsCard>
                            ))}
                        </ProductsWrapper>
                    </>
                )}
            </ProductsContainer>
        </>
    );
}

export default Problems;
