import React, { useState, useEffect } from 'react';
import {
  TableContainer,
  Table,
  TableHeader,
  TableRow,
  TableCell,
  FilterContainer,
  FilterLabel,
  Select,
  DifficultyBadge
} from './StyledElements';

const Problems = ({ problems }) => {
  const [filteredProblems, setFilteredProblems] = useState(problems);
  const [categoryFilter, setCategoryFilter] = useState('All');
  const [difficultyFilter, setDifficultyFilter] = useState('All');

  useEffect(() => {
    let filtered = problems;

    if (categoryFilter !== 'All') {
      filtered = filtered.filter(problem => problem.category === categoryFilter);
    }

    if (difficultyFilter !== 'All') {
      filtered = filtered.filter(problem => problem.difficulty === difficultyFilter);
    }

    setFilteredProblems(filtered);
  }, [categoryFilter, difficultyFilter, problems]);

  return (
    <>
      {/* Filter Section */}
      <FilterContainer>
        <div>
          <FilterLabel htmlFor="category">Filter by Category:</FilterLabel>
          <Select id="category" value={categoryFilter} onChange={(e) => setCategoryFilter(e.target.value)}>
            <option value="All">All</option>
            <option value="Array">Array</option>
            <option value="Linked List">Linked List</option>
            <option value="Dynamic Programming">Dynamic Programming</option>
            <option value="Stack">Stack</option>
            <option value="Binary Search">Binary Search</option>
            <option value="Binary Tree">Binary Tree</option>
            <option value="Intervals">Intervals</option>
            <option value="Two Pointers">Two Pointers</option>
            <option value="Sliding Window">Sliding Window</option>
            <option value="Backtracking">Backtracking</option>
          </Select>
        </div>
        <div>
          <FilterLabel htmlFor="difficulty">Filter by Difficulty:</FilterLabel>
          <Select id="difficulty" value={difficultyFilter} onChange={(e) => setDifficultyFilter(e.target.value)}>
            <option value="All">All</option>
            <option value="easy">Easy</option>
            <option value="medium">Medium</option>
            <option value="hard">Hard</option>
          </Select>
        </div>
      </FilterContainer>

      {/* Table Section */}
      <TableContainer>
        <Table>
          <thead>
            <TableRow>
              <TableHeader>Title</TableHeader>
              <TableHeader>Description</TableHeader>
              <TableHeader>Difficulty</TableHeader>
              <TableHeader>Category</TableHeader>
            </TableRow>
          </thead>
          <tbody>
            {filteredProblems.map((problem, index) => (
              <TableRow key={index}>
                <TableCell>{problem.title}</TableCell>
                <TableCell>{problem.description}</TableCell>
                <TableCell>
                  <DifficultyBadge difficulty={problem.difficulty}>
                    {problem.difficulty}
                  </DifficultyBadge>
                </TableCell>
                <TableCell>{problem.category}</TableCell>
              </TableRow>
            ))}
          </tbody>
        </Table>
      </TableContainer>
    </>
  );
};

export default Problems;
