import React, { useState, useEffect } from "react";
import axios from "axios";
import { Controlled as CodeMirror } from '@uiw/react-codemirror';
import { dracula } from '@uiw/codemirror-theme-dracula';
import 'codemirror/lib/codemirror.css';
import 'codemirror/theme/dracula.css';
import 'codemirror/mode/javascript/javascript';

const CodingEditorComponent = ({ problemId }) => {
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState('// Write your code here');
  const [output, setOutput] = useState('');

  // Fetch problem details from backend using problemId
  useEffect(() => {
    axios.get(`http://localhost:8080/lcms/coding-problems/${problemId}`)
      .then(response => {
        setProblem(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the problem details!", error);
      });
  }, [problemId]);

  // Handle code submission to backend for running/compiling
  const handleRunCode = () => {
    axios.post('http://localhost:8080/run-code', {
      code: code,
      language: "javascript",  // You can dynamically change language
      input: problem?.inputFormat // Include input data from the problem
    }).then((response) => {
      setOutput(response.data.output);  // Output of code execution
    }).catch((error) => {
      console.error("Error running code:", error);
      setOutput("Error running code");
    });
  };

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Left Section: Problem Details */}
      <div style={{ flex: 1, padding: '20px', borderRight: '1px solid #ddd' }}>
        {problem ? (
          <>
            <h1>{problem.title}</h1>
            <h3>Difficulty: {problem.difficulty}</h3>
            <p>{problem.description}</p>
            <h4>Input Format:</h4>
            <p>{problem.inputFormat}</p>
            <h4>Output Format:</h4>
            <p>{problem.outputFormat}</p>
            <h4>Example Test Cases:</h4>
            <pre>{JSON.stringify(JSON.parse(problem.exampleTestCases), null, 2)}</pre>
          </>
        ) : (
          <p>Loading problem...</p>
        )}
      </div>

      {/* Right Section: Code Editor */}
      <div style={{ flex: 1, padding: '20px' }}>
        <h2>Code Editor</h2>
        <CodeMirror
          value={code}
          options={{
            theme: 'dracula',
            mode: 'javascript',
            lineNumbers: true
          }}
          onBeforeChange={(editor, data, value) => {
            setCode(value);
          }}
        />
        <button onClick={handleRunCode} style={{ marginTop: '20px', padding: '10px 20px' }}>
          Run Code
        </button>
        <div style={{ marginTop: '20px' }}>
          <h3>Output:</h3>
          <pre>{output}</pre>
        </div>
      </div>
    </div>
  );
};

export default CodingEditorComponent;
