import styled from 'styled-components';

// Container for the entire table
export const TableContainer = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  overflow-x: auto; // Ensure table is scrollable if it's too wide
`;

// Styled table
export const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin-top: 20px;
`;

export const TableHead = styled.thead`
  background-color: #f4f4f4;
`;

export const TableRow = styled.tr`
  &:nth-child(even) {
    background-color: #f9f9f9;
  }
  &:hover {
    background-color: #e0e0e0;
  }
`;

export const TableHeader = styled.th`
  padding: 12px;
  background-color: #333;
  color: white;
  text-align: left;
  font-weight: bold;
  position: sticky; // Stick the header
  top: 0;
`;

export const TableData = styled.td`
  padding: 12px;
  text-align: left;
`;

export const DifficultyLabel = styled.span`
  padding: 5px 10px;
  color: white;
  background-color: ${(props) => {
    if (props.difficulty === 'easy') return 'green';
    if (props.difficulty === 'medium') return 'orange';
    return 'red';
  }};
  border-radius: 5px;
  text-transform: capitalize;
`;

// Filter section container
export const FilterContainer = styled.div`
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap; // Ensures filters wrap on smaller screens
  margin-bottom: 10px;
  width: 100%;
`;

export const FilterLabel = styled.label`
  font-size: 16px;
  margin-right: 10px;
`;

export const FilterSelect = styled.select`
  padding: 5px 10px;
  font-size: 16px;
  margin-right: 20px;
  border-radius: 4px;
`;
