import styled from "styled-components";

// Container for the overall products (or coding problems) section
export const ProductsContainer = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  background: #f1f1f1;
  min-height: 100vh;

  @media screen and (max-width: 768px) {
    padding: 10px;
  }
`;

// For grid-style listing of problems, keeping them aligned
export const ProductsWrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-gap: 16px;

  @media screen and (max-width: 1200px) {
    grid-template-columns: 1fr 1fr;
  }

  @media screen and (max-width: 768px) {
    grid-template-columns: 1fr;
    grid-gap: 20px;
  }
`;

// Card for each coding problem
export const ProductsCard = styled.div`
  background: #fff;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  border-radius: 8px;
  max-height: 350px;
  padding: 20px;
  margin: 20px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s ease-in-out;

  &:hover {
    transform: scale(1.05);
    transition: transform 0.2s ease-in-out;
    cursor: pointer;
  }
`;

// Heading for the coding problems section
export const ProductsH1 = styled.h1`
  font-size: 2rem;
  color: #333;
  margin-bottom: 40px;
  text-align: center;

  @media screen and (max-width: 768px) {
    font-size: 1.8rem;
    margin-bottom: 30px;
  }
`;

// Title for each coding problem
export const ProductsH2 = styled.h2`
  font-size: 1.2rem;
  color: #333;
  margin-bottom: 10px;
  font-weight: 600;
  text-align: center;
`;

// Description for each coding problem
export const ProductsP = styled.p`
  font-size: 1rem;
  text-align: center;
  color: #555;
  margin-bottom: 20px;
`;

// Container for additional information like difficulty, acceptance, etc.
export const ProductsDetails = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 100%;
  padding: 10px;
  font-size: 0.9rem;
  color: #777;
`;

// Styling for the difficulty tag (color-coded based on difficulty)
export const DifficultyTag = styled.span`
  background-color: ${({ difficulty }) =>
    difficulty === "Easy"
      ? "#d4edda"
      : difficulty === "Medium"
      ? "#fff3cd"
      : "#f8d7da"};
  color: ${({ difficulty }) =>
    difficulty === "Easy"
      ? "#155724"
      : difficulty === "Medium"
      ? "#856404"
      : "#721c24"};
  padding: 5px 10px;
  border-radius: 5px;
  font-weight: bold;
`;

// Filter container for dropdowns
export const FilterContainer = styled.div`
  display: flex;
  justify-content: space-around;
  margin-bottom: 20px;

  select {
    padding: 10px;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 1rem;
  }
`;
